const starRateElement=document.querySelector(".star-rate"),starRateItemsElement=document.querySelectorAll(".star-rate__item"),starRateClickHandler=t=>{starRateItemsElement.forEach(t=>{t.classList.remove("active","current")}),t.target.classList.add("current");for(let e=0;e<=t.target.dataset.rate-1;e++)starRateItemsElement[e].classList.add("active")};starRateElement.addEventListener("click",starRateClickHandler);